'use server'

import { supabaseAdmin } from '@/lib/supabase-admin'
import { auth } from '@/auth'
import { revalidatePath } from 'next/cache'

async function requireAdmin() {
    const session = await auth()
    const adminEmail = process.env.ADMIN_EMAIL

    if (!session?.user?.email || session.user.email !== adminEmail) {
        throw new Error('Unauthorized')
    }
}

export async function exportData() {
    await requireAdmin()

    const [
        { data: works },
        { data: posts },
        { data: news },
        { data: profile },
        { data: links }
    ] = await Promise.all([
        supabaseAdmin.from('works').select('*'),
        supabaseAdmin.from('posts').select('*'),
        supabaseAdmin.from('news').select('*'),
        supabaseAdmin.from('profile').select('*'),
        supabaseAdmin.from('links').select('*')
    ])

    return {
        works,
        posts,
        news,
        profile,
        links,
        exported_at: new Date().toISOString()
    }
}

import { backupSchema, BackupData } from '@/lib/backup-schema'

// ... existing code ...

export type ImportSummary = {
    valid: boolean
    counts: {
        works: number
        posts: number
        news: number
        profile: number
        links: number
    }
    errors?: string[]
}

export async function validateImport(jsonContent: string): Promise<ImportSummary> {
    await requireAdmin()

    let data
    try {
        data = JSON.parse(jsonContent)
    } catch (e) {
        return { valid: false, counts: { works: 0, posts: 0, news: 0, profile: 0, links: 0 }, errors: ['Invalid JSON format'] }
    }

    const result = backupSchema.safeParse(data)

    if (!result.success) {
        return {
            valid: false,
            counts: { works: 0, posts: 0, news: 0, profile: 0, links: 0 },
            errors: result.error.issues.map(e => `${e.path.join('.')}: ${e.message}`)
        }
    }

    const validData = result.data

    return {
        valid: true,
        counts: {
            works: validData.works?.length || 0,
            posts: validData.posts?.length || 0,
            news: validData.news?.length || 0,
            profile: validData.profile?.length || 0,
            links: validData.links?.length || 0
        }
    }
}

export async function executeImport(jsonContent: string) {
    await requireAdmin()

    const result = backupSchema.safeParse(JSON.parse(jsonContent))
    if (!result.success) {
        throw new Error('Validation failed during execution')
    }

    const data = result.data

    const restoreTable = async (table: string, rows: Record<string, unknown>[]) => {
        if (!rows || rows.length === 0) return
        const { error } = await supabaseAdmin.from(table).upsert(rows)
        if (error) {
            console.error(`Error restoring ${table}:`, error)
            throw new Error(`Failed to restore ${table}`)
        }
    }

    // Restore sequentially
    await restoreTable('works', data.works || [])
    await restoreTable('posts', data.posts || [])
    await restoreTable('news', data.news || [])
    await restoreTable('profile', data.profile || [])
    await restoreTable('links', data.links || [])

    revalidatePath('/', 'layout')
}

'use server'

import { supabaseAdmin } from '@/lib/supabase-admin'
import { revalidatePath } from 'next/cache'
import { redirect } from 'next/navigation'
import { auth } from '@/auth'
import { parseWorkFormData, workSchema } from '@/lib/validators'

// Helper to ensure admin (Double check in action level is good practice)
async function requireAdmin() {
    const session = await auth()
    const adminEmail = process.env.ADMIN_EMAIL

    if (!session?.user?.email || session.user.email !== adminEmail) {
        throw new Error('Unauthorized')
    }
}

import { ZodError } from 'zod'

export async function createWork(formData: FormData) {
    await requireAdmin()

    const rawData = parseWorkFormData(formData)

    const result = workSchema.safeParse(rawData)

    if (!result.success) {
        const errorMessage = result.error.issues.map(e => e.message).join(', ')
        throw new Error(errorMessage)
    }

    // Check for slug collision? Supabase unique constraint will handle it, but better to check?
    // For now, let DB unique constraint throw if duplicated, we verify via error catch.

    // Clean up empty URLs to null for DB cleaner data if needed, or keep empty string.
    // Supabase text optional is nullable or text. Text empty string is fine.

    const { error } = await supabaseAdmin
        .from('works')
        .insert({
            ...result.data,
            published_at: result.data.is_public ? new Date().toISOString() : null
        })

    if (error) {
        console.error('Create Work Error:', error)
        if (error.code === '23505') { // Unique violation
            throw new Error('Slug already exists')
        }
        throw new Error('Failed to create work')
    }

    revalidatePath('/works')
    revalidatePath('/admin/works')
    redirect('/admin/works')
}

export async function updateWork(id: string, formData: FormData) {
    await requireAdmin()

    const rawData = parseWorkFormData(formData)
    const result = workSchema.safeParse(rawData)

    if (!result.success) {
        const errorMessage = result.error.issues.map(e => e.message).join(', ')
        throw new Error(errorMessage)
    }

    const { error } = await supabaseAdmin
        .from('works')
        .update({
            ...result.data,
            updated_at: new Date().toISOString()
        })
        .eq('id', id)

    if (error) {
        console.error('Update Work Error:', error)
        if (error.code === '23505') {
            throw new Error('Slug already exists')
        }
        throw new Error('Failed to update work')
    }

    revalidatePath('/works')
    revalidatePath('/admin/works')
    redirect('/admin/works')
}

export async function deleteWork(id: string) {
    await requireAdmin()

    const { error } = await supabaseAdmin
        .from('works')
        .delete()
        .eq('id', id)

    if (error) {
        throw new Error('Failed to delete work')
    }

    revalidatePath('/works')
    revalidatePath('/admin/works')
}

import { updateWork } from '@/app/actions/works'

import { supabaseAdmin } from '@/lib/supabase-admin'
import Link from 'next/link'
import { notFound } from 'next/navigation'
import EditWorkForm from '../_components/EditWorkForm'

interface PageProps {
    params: Promise<{ id: string }>
}

export default async function EditWorkPage({ params }: PageProps) {
    const { id } = await params

    const { data: work } = await supabaseAdmin
        .from('works')
        .select('*')
        .eq('id', id)
        .single()

    if (!work) {
        notFound()
    }

    const updateAction = updateWork.bind(null, work.id)

    return (
        <div className="h-full">
            <EditWorkForm work={work} action={updateAction} />
        </div>
    )
}

'use client'

import { createWork, updateWork } from '@/app/actions/works'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Switch } from '@/components/ui/switch'
import { Textarea } from '@/components/ui/textarea'
import { Work } from '@/lib/repositories/works'
import { Loader2 } from 'lucide-react'
import Link from 'next/link'
import { useTransition } from 'react'
import { toast } from 'sonner'
import { SmartImage } from '@/components/ui/smart-image'

// Extend Work type for form props if needed, or cast handling
interface WorkWithPublic extends Work {
    is_public?: boolean
}

export default function EditWorkForm({ work, action }: { work?: WorkWithPublic, action?: any }) {
    const isEdit = !!work
    const [isPending, startTransition] = useTransition()

    // Handle action manually if not passed (for create vs update distinction wrapper?)
    // Actually the pages pass 'action' prop bound with ID for update, or just creating.
    // Spec: new/page.tsx passes createWork. [id]/page.tsx passes updateWork.bind(id).

    async function handleSubmit(formData: FormData) {
        startTransition(async () => {
            try {
                if (action) {
                    const result = await action(formData)
                    if (result?.error) {
                        toast.error(result.error)
                    } else {
                        toast.success(isEdit ? 'Work updated' : 'Work created')
                    }
                } else {
                    // Fallback if action prop not used correctly
                    if (isEdit && work) {
                        await updateWork(work.id, formData)
                        toast.success('Work updated')
                    } else {
                        await createWork(formData)
                        toast.success('Work created')
                    }
                }
            } catch (error) {
                toast.error('Failed to save work')
                console.error(error)
            }
        })
    }

    return (
        <form action={handleSubmit} className="space-y-8 max-w-3xl">
            <div className="flex items-center justify-between">
                <h1 className="text-2xl font-bold tracking-tight">{isEdit ? 'Edit Work' : 'New Work'}</h1>
                <div className="flex items-center gap-2">
                    <Button variant="outline" size="sm" asChild>
                        <Link href="/admin/works">Cancel</Link>
                    </Button>
                    <Button type="submit" size="sm" disabled={isPending}>
                        {isPending && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
                        Save
                    </Button>
                </div>
            </div>

            <Card>
                <CardContent className="pt-6 space-y-6">
                    {/* Status & Slug */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="flex items-center justify-between border rounded-lg p-4">
                            <div className="space-y-0.5">
                                <Label htmlFor="is_public" className="text-base">Public Status</Label>
                                <p className="text-xs text-muted-foreground">Visible on public site</p>
                            </div>
                            <Switch id="is_public" name="is_public" defaultChecked={work?.is_public ?? true} />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="slug">Slug (URL path)</Label>
                            <Input id="slug" name="slug" defaultValue={work?.slug} required placeholder="my-project-name" />
                        </div>
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="title">Title <span className="text-red-500">*</span></Label>
                        <Input id="title" name="title" defaultValue={work?.title} required placeholder="Project Title" />
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="description">Description</Label>
                        <Textarea id="description" name="description" defaultValue={work?.description || ''} placeholder="Project summary..." className="h-32" />
                    </div>

                    {/* URLs */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="space-y-2">
                            <Label htmlFor="deployment_url">Deployment URL</Label>
                            <Input id="deployment_url" name="deployment_url" type="url" defaultValue={work?.deployment_url || ''} placeholder="https://..." />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="github_url">GitHub URL</Label>
                            <Input id="github_url" name="github_url" type="url" defaultValue={work?.github_url || ''} placeholder="https://github.com/..." />
                        </div>
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="tech_stack">Tech Stack (Comma separated)</Label>
                        <Input id="tech_stack" name="tech_stack" defaultValue={work?.tech_stack?.join(', ') || ''} placeholder="Next.js, Tailwind, Supabase" />
                    </div>

                    {/* Image */}
                    <div className="space-y-4 pt-4 border-t">
                        <Label htmlFor="thumbnail_url">Thumbnail URL</Label>
                        <div className="flex gap-4 items-start">
                            <Input id="thumbnail_url" name="thumbnail_url" defaultValue={work?.thumbnail_url || ''} placeholder="https://..." className="flex-1" />
                        </div>
                        {work?.thumbnail_url && (
                            <div className="relative w-full aspect-video rounded-lg overflow-hidden border bg-gray-100">
                                <SmartImage
                                    src={work.thumbnail_url}
                                    alt="Preview"
                                    fill
                                    className="object-cover"
                                />
                            </div>
                        )}
                    </div>
                </CardContent>
            </Card>
        </form>
    )
}

import { ProfileRepository } from '@/lib/repositories/profile'
import { Reveal } from '@/components/ui/motion'
import { TypographyHero } from '@/components/sections/TypographyHero'
import { Button } from '@/components/ui/button'
import { ArrowRight } from 'lucide-react'
import Link from 'next/link'
import { FeaturedWorks, FeaturedWorksSkeleton } from './_components/FeaturedWorks'
import { LatestNews, LatestNewsSkeleton } from './_components/LatestNews'
import { Suspense } from 'react'

export const metadata = {
  title: 'Portfolio - Home',
  description: 'Personal portfolio and works.',
}

export default async function HomePage() {
  // Only fetch critical Profile data for Hero
  const [profile, links] = await Promise.all([
    ProfileRepository.getProfile(),
    ProfileRepository.getLinks()
  ])

  return (
    <main className="min-h-screen">
      {/* Hero Section (Typography Orchestra) */}
      <TypographyHero profile={profile} links={links} />

      {/* Featured Works with Skeleton */}
      <Suspense fallback={<FeaturedWorksSkeleton />}>
        <FeaturedWorks />
      </Suspense>

      {/* News & Updates with Skeleton */}
      <Suspense fallback={<LatestNewsSkeleton />}>
        <LatestNews />
      </Suspense>
    </main>
  )
}

'use client'

import { useState, useEffect } from 'react'
import Link from 'next/link'
import { usePathname } from 'next/navigation'
import { motion, AnimatePresence } from 'framer-motion'
import { Menu, X } from 'lucide-react'
import { cn } from '@/lib/utils'

const navItems = [
    { name: 'Works', href: '/works' },
    { name: 'Products', href: '/products' },
    { name: 'Devices', href: '/devices' },
    { name: 'News', href: '/news' },
    { name: 'Blog', href: '/blog' }, // Spec says /blog
    { name: 'About', href: '/about' },
    { name: 'Contact', href: '/contact' },
]

// To match "Blog" in spec but "Posts" in implementation, I'll link to /posts but label "Blog".
// Ideally I should rename the route, but sticking to existing /posts for stability.

export function Header() {
    const [scrolled, setScrolled] = useState(false)
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
    const pathname = usePathname()

    useEffect(() => {
        const handleScroll = () => setScrolled(window.scrollY > 20)
        window.addEventListener('scroll', handleScroll)
        return () => window.removeEventListener('scroll', handleScroll)
    }, [])

    return (
        <header
            className={cn(
                "fixed top-0 w-full z-50 transition-all duration-300 border-b border-transparent",
                scrolled ? "bg-white/80 backdrop-blur-md border-gray-100 py-3" : "bg-transparent py-5"
            )}
        >
            <div className="max-w-7xl mx-auto px-6 md:px-12 flex items-center justify-between">
                {/* Logo */}
                <Link href="/" className="text-xl font-bold tracking-tight z-50 relative group">
                    <span className="group-hover:text-indigo-600 transition-colors">Port</span>folio.
                </Link>

                {/* Desktop Nav */}
                <nav className="hidden md:flex items-center gap-1">
                    {navItems.map((item) => {
                        const isActive = pathname.startsWith(item.href)
                        return (
                            <Link
                                key={item.href}
                                href={item.href}
                                className={cn(
                                    "px-4 py-2 rounded-full text-sm font-medium transition-all hover:bg-gray-100/50",
                                    isActive ? "text-indigo-600 bg-gray-50" : "text-gray-600 hover:text-gray-900"
                                )}
                            >
                                {item.name}
                            </Link>
                        )
                    })}
                </nav>

                {/* Mobile Toggle */}
                <button
                    className="md:hidden z-50 relative p-2"
                    onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                >
                    {mobileMenuOpen ? <X /> : <Menu />}
                </button>

                {/* Mobile Nav Overlay */}
                <AnimatePresence>
                    {mobileMenuOpen && (
                        <motion.div
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            className="absolute top-0 left-0 w-full h-screen bg-white flex flex-col items-center justify-center gap-8 md:hidden"
                        >
                            {navItems.map((item, idx) => (
                                <Link
                                    key={item.href}
                                    href={item.href}
                                    onClick={() => setMobileMenuOpen(false)}
                                    className="text-2xl font-bold text-gray-900 hover:text-indigo-600 transition-colors"
                                >
                                    {item.name}
                                </Link>
                            ))}
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </header>
    )
}

'use client'

import { createContext, useContext, useEffect, useState, ReactNode } from 'react'

export type MotionPreference = 'high' | 'safe' | 'minimal'

interface MotionContextType {
    preference: MotionPreference
    setPreference: (pref: MotionPreference) => void
}

const MotionContext = createContext<MotionContextType | undefined>(undefined)

export function MotionProvider({ children }: { children: ReactNode }) {
    const [preference, setPreference] = useState<MotionPreference>('high')
    const [mounted, setMounted] = useState(false)

    useEffect(() => {
        // Load from localStorage or check media query
        const saved = localStorage.getItem('motion-preference') as MotionPreference | null
        const prefersReduced = window.matchMedia('(prefers-reduced-motion: reduce)').matches

        if (saved) {
            setPreference(saved)
        } else if (prefersReduced) {
            setPreference('minimal')
        }

        setMounted(true)
    }, [])

    const updatePreference = (pref: MotionPreference) => {
        setPreference(pref)
        localStorage.setItem('motion-preference', pref)
    }

    // Prepare context value
    const value = {
        preference,
        setPreference: updatePreference
    }

    // Prevent hydration mismatch or flash
    if (!mounted) return <div className="invisible">{children}</div>

    return (
        <MotionContext.Provider value={value}>
            {children}
            {/* Class hook for CSS-based motion control if needed */}
            <div id="motion-mode" data-mode={preference} className="hidden" />
        </MotionContext.Provider>
    )
}

export function useMotion() {
    const context = useContext(MotionContext)
    if (context === undefined) {
        throw new Error('useMotion must be used within a MotionProvider')
    }
    return context
}

'use client'

import { motion, useScroll, useTransform, useSpring, useMotionValue, useMotionTemplate } from 'framer-motion'
import { MOTION, transitions } from '@/config/motion'
import { Button } from '@/components/ui/button'
import { ArrowRight } from 'lucide-react'
import Link from 'next/link'
import { type Profile, type LinkItem as SocialLink } from '@/lib/repositories/profile'
import { useMotion as useMotionContext } from '@/components/providers/MotionProvider'
import { cn } from '@/lib/utils'

interface TypographyHeroProps {
    profile: Profile | null
    links: SocialLink[]
}

export function TypographyHero({ profile, links }: TypographyHeroProps) {
    const { preference } = useMotionContext()
    const isMinimal = preference === 'minimal'
    const isSafe = preference === 'safe' || isMinimal

    // Parallax & Mouse Interaction (High only)
    const mouseX = useMotionValue(0)
    const mouseY = useMotionValue(0)

    // Smooth mouse spring
    const smoothX = useSpring(mouseX, MOTION.spring.smooth)
    const smoothY = useSpring(mouseY, MOTION.spring.smooth)

    const handleMouseMove = (e: React.MouseEvent) => {
        if (isSafe) return
        const { clientX, clientY, currentTarget } = e
        const { width, height, left, top } = currentTarget.getBoundingClientRect()
        const x = (clientX - left) / width - 0.5
        const y = (clientY - top) / height - 0.5
        mouseX.set(x * 20) // 20px tilt
        mouseY.set(y * 20)
    }

    const title = profile?.name || 'Creator'
    // Split title into characters for orchestration
    const chars = title.split('')

    // Container for Stagger
    const container = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: {
                staggerChildren: 0.1,
                delayChildren: 0.2
            }
        }
    }

    const child = {
        hidden: {
            opacity: 0,
            y: 32, // More dramatic start
            rotateX: isSafe ? 0 : 45, // 3D rotate if safe
            filter: 'blur(12px)'
        },
        visible: {
            opacity: 1,
            y: 0,
            rotateX: 0,
            filter: 'blur(0px)',
            transition: MOTION.spring.heavy
        }
    }

    return (
        <section
            className="min-h-[85vh] flex items-center justify-center px-6 md:px-12 bg-white relative overflow-hidden"
            onMouseMove={handleMouseMove}
        >
            {/* Background Gradient (Subtle) */}
            <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(120,119,198,0.1),rgba(255,255,255,0))]" />

            <motion.div
                className="max-w-5xl w-full relative z-10"
                initial="hidden"
                animate="visible"
                variants={container}
                style={{ x: isSafe ? 0 : smoothX, y: isSafe ? 0 : smoothY }}
            >
                {/* Title Orchestra */}
                <h1 className="text-7xl md:text-9xl font-black tracking-tighter mb-8 text-slate-900 leading-[0.9] select-none cursor-default mix-blend-multiply">
                    {chars.map((char, i) => (
                        <motion.span
                            key={i}
                            variants={child}
                            className="inline-block origin-bottom"
                            whileHover={!isSafe ? {
                                scale: 1.1,
                                rotate: Math.random() * 10 - 5,
                                color: "#4f46e5", // Indigo-600
                                transition: MOTION.spring.rapid
                            } : undefined}
                        >
                            {char === " " ? "\u00A0" : char}
                        </motion.span>
                    ))}
                </h1>

                {/* Subtitle & Socials */}
                <motion.div variants={child} transition={MOTION.spring.smooth}>
                    <p className="text-xl md:text-3xl text-gray-500 font-light max-w-2xl mb-12 leading-relaxed tracking-wide">
                        {profile?.bio_short || 'Building digital experiences that feel alive.'}
                    </p>

                    <div className="flex flex-col md:flex-row items-center gap-6 justify-start">
                        <Button
                            asChild
                            size="lg"
                            className="rounded-full px-10 text-lg h-14 bg-slate-900 hover:bg-indigo-600 transition-all shadow-xl hover:shadow-indigo-200 hover:scale-105 active:scale-95 duration-200"
                        >
                            <Link href="/works">
                                View Selected Works <ArrowRight className="ml-2 w-5 h-5" />
                            </Link>
                        </Button>

                        <div className="flex gap-3 flex-wrap justify-center">
                            {links.map((link) => (
                                <motion.a
                                    key={link.id}
                                    href={link.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="px-4 py-2 text-gray-500 hover:text-slate-900 transition-colors bg-gray-50/50 border border-gray-100 rounded-full text-sm font-medium hover:bg-white hover:border-gray-300 backdrop-blur-sm"
                                    whileHover={{ y: -2 }}
                                    whileTap={{ scale: 0.95 }}
                                >
                                    {link.title}
                                </motion.a>
                            ))}
                        </div>
                    </div>
                </motion.div>
            </motion.div>
        </section>
    )
}

'use client'

import { useState } from 'react'
import { Button } from '@/components/ui/button'
import { Copy, Check } from 'lucide-react'
import { toast } from 'sonner'

export function CopyButton({ text, label = 'Copy' }: { text: string, label?: string }) {
    const [copied, setCopied] = useState(false)

    const handleCopy = async () => {
        try {
            await navigator.clipboard.writeText(text)
            setCopied(true)
            toast.success('Copied to clipboard!')
            setTimeout(() => setCopied(false), 2000)
        } catch (err) {
            toast.error('Failed to copy')
        }
    }

    return (
        <Button
            onClick={handleCopy}
            variant="outline"
            className="flex items-center gap-2"
        >
            {copied ? <Check className="w-4 h-4 text-green-500" /> : <Copy className="w-4 h-4" />}
            {copied ? 'Copied!' : label}
        </Button>
    )
}

'use client'

import { useMotion, MotionPreference } from "@/components/providers/MotionProvider"
import { Button } from "@/components/ui/button"
import { motion, AnimatePresence } from "framer-motion"
import { Settings2, Zap, Shield, Battery } from "lucide-react"
import { useState } from "react"
import { cn } from "@/lib/utils"

export function MotionToggle() {
    const { preference, setPreference } = useMotion()
    const [isOpen, setIsOpen] = useState(false)

    const toggleOpen = () => setIsOpen(!isOpen)

    const cyclePreference = () => {
        if (preference === 'high') setPreference('safe')
        else if (preference === 'safe') setPreference('minimal')
        else setPreference('high')
    }

    const options = [
        { value: 'high', label: 'High', icon: Zap, desc: 'Full Motion' },
        { value: 'safe', label: 'Safe', icon: Shield, desc: 'No Parallax/Scale' },
        { value: 'minimal', label: 'Minimal', icon: Battery, desc: 'Fade Only' },
    ]

    const currentOption = options.find(o => o.value === preference) || options[0]
    const Icon = currentOption.icon

    return (
        <div className="fixed bottom-4 left-4 z-50">
            {/* Expansion logic can be added, for now simple cycler */}
            <Button
                variant="outline"
                size="sm"
                onClick={cyclePreference}
                className={cn(
                    "flex items-center gap-2 bg-background/80 backdrop-blur-md border-gray-200 shadow-sm hover:bg-gray-100 transition-all",
                    preference === 'high' && "text-indigo-600 border-indigo-100",
                    preference === 'safe' && "text-emerald-600 border-emerald-100",
                    preference === 'minimal' && "text-gray-500 border-gray-200"
                )}
                title={`Motion Mode: ${currentOption.label} - ${currentOption.desc}`}
            >
                <Icon className="w-4 h-4" />
                <span className="text-xs font-medium">{currentOption.label}</span>
            </Button>
        </div>
    )
}
